mapper.insert:
		insert into tbl_answer ( 
			content, rgno 
		) values ( 
			#{content}, #{rgno}
		)


        SELECT LAST_INSERT_ID()

mapper.select:
select *
  from tbl_answer
 where asno = #{asno} and rgno = #{rgno}

mapper.update:
update tbl_answer 
   set content = #{content}
 where asno = #{asno} and rgno = #{rgno}

mapper.delete:
delete 
  from tbl_answer 
 where asno = #{asno} and rgno = #{rgno}
