mapper.select:
select *
  from tbl_reply
 where bno = #{bno}
 order by rno desc
 limit #{cri.pageStart}, #{cri.perPageNum}


select count(rno) 
  from tbl_reply 
 where bno = #{bno}


 		select *
    	  from tbl_reply
    	 where bno =  #{bno}
    	 order by rno desc
		 limit #{cri.pageStart}, #{cri.perPageNum}

mapper.insert:
insert into tbl_reply (
	bno, reply, replyer
) values (
	#{bno}, #{reply}, #{replyer}
)

mapper.update:
update tbl_reply 
   set reply = #{reply}
 where rno = #{rno}
